function gridLayout(){var t=window.innerWidth-side-rightMargin,e=window.innerHeight-2*mainHeaderH;document.getElementById("mainProjectGridContainer").style.width=t.toString().concat("px");var n=boxes.length,o=[];for(i=1;i<=n;i++)(0===o.length||Math.ceil(n/i)<Math.ceil(n/(i-1)))&&o.push([i,Math.ceil(n/i)]);var r=[];for(i=0;i<o.length;i++)(0===r.length||Math.min(t/(o[i][0]+1),e/o[i][1])>Math.min(t/(r[0]+1),e/r[1]))&&(r=[o[i][0],o[i][1]]);if(t/(r[0]+1)<=e/r[1])var a=t/window.innerWidth/(r[0]+1)*80,g="vw";else var a=e/window.innerHeight/r[1]*80,g="vh";for(i=0;i<boxes.length;i++)boxes[i].style.width=a.toString().concat(g),boxes[i].style.height=a.toString().concat(g),boxes[i].style.marginLeft=(a/8).toString().concat(g),boxes[i].style.marginRight=(a/8).toString().concat(g),boxes[i].style.marginBottom=(2*a/8).toString().concat(g)}var side=320,rightMargin=80,mainHeaderH=37,boxes=document.querySelectorAll(".mainProjectGridBlock");$(window).resize(function(i){gridLayout()}),$(document).ready(function(i){gridLayout()});